  const offset = zeroIndexedOffsetPage * perPage;

  const isPartial = url.searchParams.get("partial") === "true";
  const isFirstPage = !pageInfo.previousPage;

  const currentPage = pageInfo?.currentPage ? pageInfo?.currentPage - 1 : 1;

